    /**
    * 
    * @param mixed $value
    */
    public function setMenuhostCtrl($value){
        $mhostctrl=$this->getParam("menuhostctrl");
        if($mhostctrl != $value){
            if(is_object($mhostctrl)){
                $mhostctrl->unregView($this);
            }
            $mhostctrl=$value;
            if($mhostctrl != null)
                $mhostctrl->regView($this, "setMenuview");
            $this->setParam("menuhostctrl", $mhostctrl);
        }
    }
    ///<summary></summary>
    /**
