        if(!igk_is_conf_connected())
            return;
        $ad=igk_get_data_adapter($this);
        $t="success";
        $msg["success"]=__("Table clear");
        $msg["danger"] =__("Table not found");
        $e=0;
        if($ad->connect()){
            if(!($e=$ad->clearTable($this->getDataTableName()))){
                $t="danger";
            }
            $ad->close();
        }
        if(igk_is_ajx_demand()){
