    ///<summary>load theme by name</summary>
    /**
    * load theme by name
    */
    public function loadTheme(){
        $b=igk_app();
        $doc = igk_app()->getDoc();
        if($doc && !empty($n = $doc->Theme->Name)){
            $f=igk_io_syspath(IGK_RES_FOLDER."/Themes/".$n.".".IGK_DEFAULT_VIEW_EXT);
            if(file_exists($f)){
                $doc->Theme->LoadThemeFromFile($f);
                return 1;
            }
        }
        return 0;
    }
