    ///<summary></summary>
    ///<param name="searchkey" default="null"></param>
    /**
    * 
    * @param mixed $searchkey the default value is null
    */
    public function search($searchkey=null){
        $q=igk_getr("q", $searchkey);
        if(empty($q)){
            $this->m_search=null;
            $v_sh=null;
            $this->View();
            return;
        }
        $tab=igk_app()->Doc->Theme->def->Attributes->ToArray();
        $out=array();
        foreach($tab as $k=>$v){
            if(strstr($k, $q)){
                $out[$k]=$v;
            }
        }
        $this->m_search=$out;
        $v_sh=$q;
        $this->View();
    }
