    ///<summary></summary>
    /**
    * 
    */
    public function themeCurrentMedia(){
        if(IGKUserAgent::IsIE())
            return "ie";
        if(IGKUserAgent::IsMod())
            return "mod";
        if(IGKUserAgent::IsChrome())
            return "chr";
        if(IGKUserAgent::IsSafari())
            return "saf";
        if(IGKUserAgent::IsAndroid())
            return "android";
        if(IGKUserAgent::IsIOS())
            return "ios";
        return "ie";
    }
