    ///<param name="obj"></param>
    /**
    * 
    * @param mixed $obj
    */
    public function getId($obj){
        if($obj === null)
            return null;
		if (get_class($obj) === stdClass::class){
			igk_die("stdlass resolv ::: -" );
		}

        $r=$obj->getParam(__CLASS__.":id");
        if($r)
            return $r;
        foreach($this->m_ids as $k=>$v){
            if($v === $obj)
