///<summary>Represente class: IGKDataBindingScript</summary>
/**
* Represente IGKDataBindingScript class
*/
final class IGKDataBindingScript extends IGKObject{
    private $_args;
    private $_shifparent;
    ///<summary></summary>
    /**
    * 
    */
    public function __construct(){
        $this->_args=array();
        $this->_shifparent=0;
    }
    ///<summary></summary>
    ///<param name="n"></param>
    /**
    * 
    * @param mixed $n
    */
    public function __get($n){
        if($n === "args")
            return $this->peek();
        return null;
    }
    ///<summary></summary>
    ///<param name="n"></param>
    /**
    * 
    * @param mixed $n
    */
    public function __isset($n){
        return ($n === 'args');
    }
    ///<summary></summary>
    /**
    * 
    */
    public function Count(){
        return count($this->_args);
    }
    ///<summary></summary>
    /**
    * 
    */
    public function getArgs(){
        return $this->peek();
    }
    ///<summary></summary>
    /**
    * 
    */
    public function peek(){
        if((($c=count($this->_args)) - $this->_shifparent) > 0){
            return $this->_args[$c - (1 + $this->_shifparent)];
        }
        return null;
    }
    ///<summary></summary>
    /**
    * 
    */
    public function pop(){
        return array_pop($this->_args);
    }
    ///<summary></summary>
    ///<param name="data"></param>
    /**
    * 
    * @param mixed $data
    */
    public function push($data){
        array_push($this->_args, $data);
    }
    ///<summary></summary>
    /**
    * 
    */
    public function resetShift(){
        $this->_shifparent=0;
    }
    ///<summary></summary>
    /**
    * 
    */
    public function shiftParent(){
        $this->_shifparent=1;
    }
}
///<summary>represent datetime utility class</summary>
/**
* represent datetime utility class
*/
class IGKDateTime extends IGKObject {
    private $m_day;
    private $m_hour;
    private $m_min;
    private $m_month;
    private $m_sec;
    private $m_year;

    public static function GetAge($birthdate){
        return (new DateTime())->diff(new DateTime($birthdate))->y;
    }

    ///<summary></summary>
    /**
    * 
    */
    private function __construct(){}
    ///<summary>display value</summary>
    /**
    * display value
    */
    public function __toString(){
        return "IGKDateTime:[".$this->day."-".$this->month."-".$this->year."]";
    }
    ///<summary></summary>
    ///<param name="date1"></param>
    ///<param name="date2"></param>
    /**
    * 
    * @param mixed $date1
    * @param mixed $date2
    */
    public static function compareDate($date1, $date2){
        if(!$date1 || !$date2)
            return -2;
        $s1=$date1->getDate("Ymd");
        $s2=$date2->getDate("Ymd");
        return strcmp($s1, $s2);
    }
    ///<summary></summary>
    ///<param name="format"></param>
    ///<param name="value"></param>
    /**
    * 
    * @param mixed $format
    * @param mixed $value
    */
    public static function CreateFrom($format, $value){
        $tab=(object)date_parse_from_format($format, $value);
        if($tab->error_count == 0){
            $d=new IGKDateTime();
            $d->m_day=$tab->day;
            $d->m_month=$tab->month;
            $d->m_year=$tab->year;
            $d->m_min=$tab->minute;
            $d->m_sec=$tab->second;
            $d->m_hour=$tab->hour;
            return $d;
        }
        else{
            if(IGKApp::$DEBUG)
                igk_show_prev($tab);
        }
        return null;
    }
    ///<summary></summary>
    ///<param name="format"></param>
    /**
    * 
    * @param mixed $format
    */
    public function getDate($format){
        $s=$format;
        $s=str_replace("Y", $this->year, $s);
        $s=str_replace("m", $this->month, $s);
        $s=str_replace("d", $this->day, $s);
        $s=str_replace("H", $this->hour, $s);
        $s=str_replace("i", $this->min, $s);
        $s=str_replace("s", $this->sec, $s);
        return $s;
    }
    ///<summary></summary>
    /**
    * 
    */
    public function getday(){
        return $this->m_day;
    }
    ///<summary></summary>
    /**
    * 
    */
    public function gethour(){
        return $this->m_hour;
    }
    ///<summary></summary>
    /**
    * 
    */
    public function getmin(){
        return $this->m_min;
    }
    ///<summary></summary>
    /**
    * 
    */
    public function getmonth(){
        return $this->m_month;
    }
    ///<summary></summary>
    /**
    * 
    */
    public function getsec(){
        return $this->m_sec;
    }
    ///<summary></summary>
    /**
    * 
    */
    public function getyear(){
        return $this->m_year;
    }
    ///<summary></summary>
    ///<param name="date1"></param>
    ///<param name="date2"></param>
    /**
    * 
    * @param mixed $date1
    * @param mixed $date2
    */
    public static function isDateEqual($date1, $date2){
        return self::compareDate($date1, $date2) == 0;
    }
    ///<summary></summary>
    ///<param name="date1"></param>
    ///<param name="date2"></param>
    /**
    * 
    * @param mixed $date1
    * @param mixed $date2
    */
    public static function isDateMonthEqual($date1, $date2){
        return (self::IsDateYearEqual($date1, $date2) === true) && ($date1->month == $date2->month);
    }
    ///<summary></summary>
    ///<param name="date1"></param>
    ///<param name="date2"></param>
    /**
    * 
    * @param mixed $date1
    * @param mixed $date2
    */
    public static function isDateYearEqual($date1, $date2){
        if(!$date1 || !$date2)
            return -2;
        return $date1->year == $date2->year;
    }
}
