    public function __set($key, $value){
        if(IGKString::StartWith($key, "igk:")){
            $this->setSysAttribute(substr($key, 4), $value, $this->LoadingContext);
            return;
        }
        if(!$this->_setIn($key, $value)){
            $o=null;
            if(!$this->evalCallback("set".$key, $o, array("value"=>$value))){
                $this->$key=$value;
            }
        }
