        $g=$this->getFlag(IGK_CALLBACK_FLAG);
        $k = $n."Params";
        if($callable == null){
            unset($g[$n]);
            $this->setParam($k, null);
        }
        else{
            $g[$n]=$callable;
            $tb=array_slice(func_get_args(), 2);
            if((count($tb) > 0) && is_array($tb[0])){
                $this->setParam($k, $tb[0]);
            }
            if(!$g){
                $g=array();
            }
        }

        if(igk_count($g) == 0)
            $this->unsetFlag(IGK_CALLBACK_FLAG);
        else
            $this->setFlag(IGK_CALLBACK_FLAG, $g);
        return $this;
    }
