    protected function __sortChild(){
        if($this->SortRequired){
            $g=$this->Childs;
            if($g)
                igk_usort($g, array("IGKHtmlItem", "SortChild"));
            $this->unsetFlag(IGK_SORTREQUIRED_FLAG);
        }
    }
