    ///<param name="value"></param>
    /**
    * 
    * @param mixed $name
    * @param mixed $value
    */
    public function getElementByAttribute($name, $value){
        if(($c=$this->getChilds()) == null){
            return null;
        }
        $tab=array();
        $s=strtolower($value);
        foreach($c as $k){
            if(strtolower($k[$name]) == $s)
                $tab[]=$k;
            $r=$k->getElementByAttribute($name, $s);
            if(is_array($r))
                $tab=array_merge($tab, $r);
            else if($r)
                $tab[]=$r;
        }
        if(count($tab) == 1)
            return $tab[0];
        return $tab;
    }
    ///<summary></summary>
    ///<param name="id"></param>
    /**
    * 
    * @param mixed $id
    */
    public function getElementById($id){
        $c=$this->getChilds();
        if($c == null){
            return null;
        }
        $tab=array();
        $s=strtolower($id);
        $q=$this;
        $p=null;
        $itab=array($q);
        while($q=array_pop($itab)){
            if(strtolower($q["id"]) == $s){
                $tab[]=$q;
                continue;
            }
            if($c=$q->Childs){
                foreach($c as $k){
                    array_push($itab, $k);
                }
            }
        }
        if(count($tab) == 1)
            return $tab[0];
        return $tab;
    }
    ///<summary></summary>
    ///<param name="name"></param>
    ///<return refout="true"></return>
    /**
    * 
    * @param mixed $name
    * @return *
    */
    public function & getElementsByTagName($name){
        $tab=array();
        $s=strtolower($name);
        $c=$this->getChilds();
        if($c){         
            if($name == "*"){
                $tab=array_merge($tab, $c->ToArray());
                foreach($c as $k){
                    $c=$k->getElementsByTagName($s);
                    if(is_array($c) && (count($c) > 0))
                        $tab=array_merge($tab, $c);
                }
            }
            else{
                foreach($c as $k){
                    if(strtolower($k->TagName) == $s){
                        $tab[]=$k;
                    }
                    $c=$k->getElementsByTagName($s);
                    if(is_array($c) && (count($c) > 0))
                        $tab=array_merge($tab, $c);
                }
            }
        }
        return $tab;
    }
