    * utility function
    */
    public function loadArray($tab, $callback=null){
        if($tab == null){
            return;}
        if($callback){
            foreach($tab as $k=>$v){
                if(!$callback($this, $k, $v)){
                    $this->addNode($k)->Content=$v;
                }
            }
        }
        else{
            foreach($tab as $k=>$v){
                $this->addNode($k)->Content=$v;
            }
        }
    }
