    /**
    * 
    * @param mixed $name
    */
    public function getElementById($name){
        $tab=array();
        $r1=$this->m_head->getElementById($name);
        $r2=$this->m_body->getElementById($name);
        if(is_array($r1))
            array_merge($tab, $r1);
        else
            $tab[]=$r1;
        if(is_array($r2))
            $tab=array_merge($tab, $r2);
        else
            $tab[]=$r2;
        if(count($tab) == 1)
            return $tab[0];
        return $tab;
    }
    ///<summary></summary>
