    ///<return refout="true"></return>
    /**
    * 
    * @param mixed $name
    * @return *
    */
    public function & getElementsByTagName($name){
        $n=strtolower($name);
        $tab=array();
        if($n == "head"){
            $tab []=$this->m_head;
            return $tab;
        }
        if($n == "body"){
            $tab []=$this->m_body;
            return $tab;
        }
        $tab1=$this->m_head->getElementsByTagName($name);
        $tab2=$this->m_body->getElementsByTagName($name);
        $h=array_merge($tab1, $tab2);
        return $h;
    }
