    public static function Create($file){
        if(is_file($file)){
            $f=fopen($file, "r");
            if($f){
                $c=filesize($file);
                $str=fread($f, $c);
                $reader=new IGKHtmlReader($str);
                $reader->m_hfile=$f;
                return $reader;
            }
        }
        return null;
