    public function getAssoc(){
        if(($i=$this->getFlag(self::JSMAN_ASSOC_TABLE_FLAG)) == null){
            $i=new IGKHtmlScriptAssocInfo();
            $this->_f->setFlag(self::JSMAN_ASSOC_TABLE_FLAG, $i);
