    /**
    * set session param
    */
    public function setParam($key, $value){
        if(empty($key))
            return;
        if(isset($this->m_sessionParams[$key])){
            if($value == null)
                unset($this->m_sessionParams[$key]);
            else
                $this->m_sessionParams[$key]=$value;
        }
        else{
            if($value != null){
                $this->m_sessionParams[$key]=$value;
            }
        }
    }
