final class IGKHtmlDocTheme extends IGKObjectGetProperties implements ArrayAccess {
    const REGKEY="HtmlDocTheme";
    private $m_document;
    private $m_def;
    private $m_id;
    private $m_medias;
    private $m_type;
    private $m_istemp;
    private static $MEDIA;
    private static $SM_MEDIAKEY;
    ///<summary></summary>
