    public function getAllClassExpression(){
        $out=IGK_STR_EMPTY;
        $def=$this->def;
        $tab=igk_createnode("table");
        foreach($def->Attributes as $k=>$v){
            $r=$tab->addRow();
            $r->addTd()->Content=$k;
            $r->addTd()->Content=$v;
        }
        $out .= $tab->Render();
        return $out;
