///<summary>represnet xml node type</summary>
/**
* represnet xml node type
*/
final class IGKXMLNodeType{
    const CDATA=5;
    const COMMENT=3;
    const DOCTYPE=7;
    const ELEMENT=1;
    const ENDELEMENT=4;
    const NONE=-1;
    const PROCESSOR=2;
    const TEXT=6;
    ///<summary></summary>
    ///<param name="i"></param>
    /**
    * 
    * @param mixed $i
    */
    public static function GetString($i){
        switch($i){
            case self::NONE:
            return "NONE";
            case self::ELEMENT:
            return "ELEMENT";
            case self::PROCESSOR:
            return "PROCESSOR";
            case self::COMMENT:
            return "COMMENT";
            case self::ENDELEMENT:
            return "ENDELEMENT";
            case self::CDATA:
            return "CDATA";
            case self::TEXT:
            return "TEXT";
            case self::DOCTYPE:
            return "DOCTYPE";
        }
        return "UNKNOWN";
    }
}
